mapper.select:
SELECT uId, dId, name, state, dtId, cmpCode
FROM device
WHERE uId LIKE #{uId};

mapper.update:
UPDATE device
SET
state = #{state}
WHERE dId LIKE #{dId} AND uId LIKE #{uId};

mapper.select:
SELECT dtId
FROM device
WHERE dId LIKE #{dId} AND uId LIKE #{uId};

mapper.delete:
delete from device
WHERE
dId LIKE #{dId} AND uId  #{uId};
